package util;

import java.util.Scanner;

public class NSLookup {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		while(true) {
			System.out.println(">> ");
			String line = sc.nextLine();
			if("quit".equals(line)) {
				break;
			}
		}
	}
	//String host (도메인네임) 출력
	

}
